import './css/App.css'
import React, { useRef, useEffect } from 'react';

function Pokedex() {
    const fileInputRef = useRef(null);

    useEffect(() => {

        if (fileInputRef.current) {
            fileInputRef.current.addEventListener('change', handleFileSelect);
        }

        uploadImage(selectedFile);

        return () => {
            if (fileInputRef.current) {
                fileInputRef.current.removeEventListener('change', handleFileSelect);
            }
        };

    });

    const onTakePhoto = (e) => {
        alert("HELLO");
        const file = e.target.files[0];
        if (file) {
            alert("FILE SELECTED");
            // uploadImage(selectedFile);
        } else {
            console.error('No file selected');
        }
    }

    return (
        <div id="Pokedex">
            <div id="left_block">
                <div id="camera">
                    <div id="outer_circle"></div>
                    <div id="inner_circle"></div>
                </div>
            </div>
            <div id="spine">
                <div id="spine_upper"></div>
                <div id="spine_lower"></div>
            </div>
            <div id="right_block">
                <div id="right_block_before_corner"></div>
                <div id="right_triangle_corner">
                    <div id="triangle_corner"></div>
                    <div id="triangle_corner_body"></div>
                    <button id="upload_photo" onClick={onTakePhoto}>Take Photo</button>
                </div>
                <div id="right_block_after_corner"></div>
            </div>
        </div>
    )
}

export default Pokedex;
